	(17797, 0, 0, 'Surge forth my pets!', 'Jaillissez mes chéris !', NULL, NULL, NULL, NULL, NULL, NULL),
	(17797, 1, 0, 'The depths will consume you!', 'Les profondeurs vous dévorerons !', NULL, NULL, NULL, NULL, NULL, NULL),
	(17797, 1, 1, 'Meet your doom, surface dwellers!', 'Périssez, habitants de la surface !', NULL, NULL, NULL, NULL, NULL, NULL),
	(17797, 1, 2, 'You will drown in blood!', 'Vous vous noierez dans le sang !', NULL, NULL, NULL, NULL, NULL, NULL),
	(17797, 2, 0, 'To the depths of oblivion with you!', 'Plongez dans les profondeurs de l\'oubli !', NULL, NULL, NULL, NULL, NULL, NULL),
	(17797, 2, 1, 'For my lady and master!', 'Pour Madame et Souveraine.', NULL, NULL, NULL, NULL, NULL, NULL),
