	(29313, 2, 0, 'I... recede.', 'Je... reflue.', NULL, NULL, NULL, NULL, NULL, NULL),
	(29313, 3, 0, 'I... am fury... unrestrained!', 'Je suis... la fureur... irrépressible !', NULL, NULL, NULL, NULL, NULL, NULL),
	(29313, 4, 0, 'I shall consume, decimate, devastate, and destroy! Yield now to the wrath of the pounding sea!', 'Je vais dévorer, décimer, dévaster et détruire ! Cédez face au courroux et au martèlement de la mer !', NULL, NULL, NULL, NULL, NULL, NULL),
	(29313, 5, 0, 'I will not be contained! Ngyah!!', 'Rien ne me retiendra !', NULL, NULL, NULL, NULL, NULL, NULL),
